package com.yan.highprivacytestimport android.content.Contextimport com.yan.highprivacy.PrivacyMgr/** * @author Bevan (Contact me: https://github.com/genius158) * @since 2020/9/22 */class PrivacyApplicationHock : TestApp() {    init {        // 设置权限Activity class        PrivacyMgr.privacy.highPrivacyActivityClass = { PrivacyActivity::class.java }        // 不拦截的activity class 列表，比如我们要跳转的H5隐私权益界面        PrivacyMgr.privacy.interceptIgnoreActivityClass = { arrayOf() }    }    /**     * 固定实现     */    override fun onCreate() {        PrivacyMgr.appLife.onCreate(this) { super.onCreate() }    }    /**     * 固定实现     */    override fun attachBaseContext(base: Context) {        PrivacyMgr.appLife.onAttachContext(this, base) { super.attachBaseContext(base) }    }}