package com.yan.highprivacyimport android.app.Activityimport android.app.AlertDialogimport android.os.Bundle/** * @author Bevan (Contact me: https://github.com/genius158) * @since  2020/9/22 * PrivacyActivity 由业务具体实现 * 该类为实现样例，最高权限判断activity */class PrivacyActivity : Activity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        AlertDialog.Builder(this)            .setPositiveButton("确定") { id, vtb ->                finish()                PrivacyMgr.privacy.dispatchAuth()            }            .setNegativeButton("取消") { id, vtb ->                finish()            }            .show()    }}